import React from "react";

// Using Route in order to define the different routes of our application
import { Route, Routes } from "react-router-dom";

// Import all the components we need in our app
import Navbar from "./components/navbar";
import RecordList from "./components/recordList";
import Edit from "./components/edit";
import Create from "./components/create";
import Home from "./components/home";

// Define the main App component
const App = () => {
  // Return the main App component, which includes a navbar and routes for the different pages
  return (
    <div style={{ 
      backgroundImage: `url(${'https://img.freepik.com/free-photo/space-frame-office-equipment-placed-brown-plywood-floor_1150-17058.jpg?w=1380&t=st=1678631265~exp=1678631865~hmac=4770a247df64eed9e7541c9b6cd9c6aac8d1ca8600bbd4fd3371c40f5044377e'})`, 
      backgroundSize: 'cover',
      minHeight: '100vh',
    }}>
      <Navbar />
      <div style={{ margin: 20 }}>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/recordList" element={<RecordList />} />
        <Route path="/edit/:id" element={<Edit />} />
        <Route path="/create" element={<Create />} />
      </Routes>
      </div>
    </div>
  );
};

// Export the main App component
export default App;
